package laboratory;import java.sql.*;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class DatabaseUtilities {    private static final Logger logger = LoggerFactory.getLogger(DatabaseUtilities.class);    public static DatabaseResults getQueryResults(String query, Connection connection) {        try {            Statement statement = connection.createStatement();            ResultSet resultSet = statement.executeQuery(query);            ResultSetMetaData resultsMetaData = resultSet.getMetaData();            int columnCount = resultsMetaData.getColumnCount();            String[] columnNames = new String[columnCount];            for (int i = 1; i < columnCount + 1; i++) {                columnNames[i - 1] = resultsMetaData.getColumnName(i).trim();            }            DatabaseResults dbResults = new DatabaseResults(columnCount, columnNames);            while (resultSet.next()) {                String[] row = new String[columnCount];                for (int i = 1; i < columnCount + 1; i++) {                    String entry = resultSet.getString(i);                    if (entry != null) {                        entry = entry.trim();                    }                    row[i - 1] = entry;                }                dbResults.addRow(row);            }            return (dbResults);        } catch (SQLException e) {            logger.error("A generic exception occurred.", e);            return (null);        }    }    public static void recordDB(String query, Connection connection) {        try {            PreparedStatement stmt = connection.prepareStatement(query);            stmt.execute();        } catch (SQLException e) {            logger.error("A generic exception occurred.", e);        }    }}